CREATE FUNCTION quote(text) RETURNS text
    LANGUAGE plpgsql STABLE STRICT
    AS $_$
  DECLARE
    value ALIAS FOR $1;
    output TEXT;
  BEGIN
    IF value IS NOT NULL AND value <> '' THEN
      RETURN '##' || value || '##';
    ELSE
      RETURN '';
    END IF;
  END;
$_$;


