  use MARC::File::XML;

  my $xml = $marcxml;
  eval { 
    my $marc = MARC::Record->new_from_xml($marcxml, 'UTF-8');
    my $f008 = $marc->field('008');
    if (!defined($f008) or $f008->data =~ /^\s*$/) {
