CREATE FUNCTION parse_contact(lines text[]) RETURNS text[]
    LANGUAGE plpgsql IMMUTABLE STRICT
    AS $$
DECLARE
        role TEXT := '';
        email TEXT := '';
        phone TEXT := '';
        line_count INTEGER := array_upper(lines, 1);
        i INTEGER;
