--
-- Name: link_circ_limit_groups(bigint, integer[]); Type: FUNCTION; Schema: action; Owner: postgres
--

CREATE FUNCTION link_circ_limit_groups(bigint, integer[]) RETURNS void
    LANGUAGE sql
    AS $_$
